mapper.insert:
    INSERT INTO ANNIVERSARY
    (
	    NO
	    ,WRITER_NO
	    ,TITLE
	    ,CONTENT
	    ,CHECK_DATE
	    ,CODE
    )
	VALUES
	(
		SEQ_ANNIVERSARY_NO.NEXTVAL
		,#{writerNo}
		,#{title}
		,#{content}
		,#{checkDate}
		,#{code}
	)

mapper.update:
  	UPDATE ANNIVERSARY
	SET 
		TITLE = #{title}
		,CONTENT = #{content}
		,CHECK_DATE = #{checkDate}
	WHERE DEL_YN = 'N'
	AND WRITER_NO = #{writerNo}
