mapper.select:
SELECT S.TITLE, S.CONTENT, TO_CHAR(S.CHECK_DATE, 'YYYY-MM-DD') AS CHECK_DATE, S.CODE, S.CATEGORY FROM SCHEDULE S WHERE S.CODE = #{code}
UNION ALL
SELECT A.TITLE, A.CONTENT, TO_CHAR(A.CHECK_DATE, 'YYYY-MM-DD') AS CHECK_DATE, A.CODE, A.CATEGORY FROM ANNIVERSARY A WHERE A.CODE = #{code}
UNION ALL
SELECT D.TITLE, D.CONTENT, TO_CHAR(D.CHECK_DATE, 'YYYY-MM-DD') AS CHECK_DATE, D.CODE, D.CATEGORY FROM DIARY D WHERE D.CODE = #{code}
UNION ALL
SELECT T.TITLE, T.CONTENT, TO_CHAR(T.CHECK_DATE, 'YYYY-MM-DD') AS CHECK_DATE, T.CODE, T.CATEGORY FROM TODO T WHERE T.CODE = #{code}
