mapper.insert:
INSERT INTO MEMBER 
(NO, ID, PWD, NICK, NAME, BIRTH_DATE, ADDRESS, PHONE, EMAIL) 
VALUES (
SEQ_MEMBER_NO.NEXTVAL
, #{id}
, #{pwd}
, #{nick}
, #{name}
, #{birthDate}
, #{address}
, #{phone}
, #{email}
)

mapper.select:
SELECT * FROM MEMBER 
WHERE ID = #{id} 
AND PWD = #{pwd} 
AND QUIT_YN = 'N'

SELECT COUNT(*) AS CNT 
FROM MEMBER 
WHERE ID = #{id}

mapper.update:
UPDATE MEMBER 
SET PWD = #{pwd}  
WHERE NO = #{no}
