mapper.insert:
  	INSERT INTO SCHEDULE
  	(
  		NO
  		,WRITER_NO
  		,TITLE
  		,CONTENT
		,CHECK_DATE
  		,CODE 
  	)
  	VALUES
  	(
  		SEQ_SCHEDULE_NO.NEXTVAL
  		,#{writerNo}
  		,#{title}
  		,#{content}
  		,#{checkDate}
  		,#{code}
  	)

mapper.update:
  	UPDATE SCHEDULE
	SET 
		TITLE = #{title} 
		,CONTENT = #{content}
		,CHECK_DATE = #{checkDate}
	WHERE DEL_YN = 'N'
	AND CODE = #{code}
	AND NO = #{no}
